import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import { FiArrowLeft} from 'react-icons/fi'

import logoImg from '../../assets/logo.svg'
import './styles.css';

export default function NewIncident(){
  const [tittle, setTittle] = useState('')
  const [description, setDescription] = useState('')
  const [value, setValue] = useState('')


  return (
    <div className="new-incident-container">
      <div className="content">
        <section>
          <img src={logoImg} alt="Be the Hero"/>

          <h1>Cadastrar novo caso</h1>
          <p>Descreva o caso detalhadamente para encotnrar um herói para resolver isso.</p>

          <Link className="back-link" to="/profile">
            <FiArrowLeft size={16} color='#e02041'/>
            Voltar para home
          </Link>

          </section>
        <form>
          <input 
            placeholder="Título do caso"
            value={tittle}
            oncChange={e =>setTittle(e.target.tittle)}
          />
          <textar
            ea placeholder="Descrição"
            value={description}
            oncChange={e =>setTittle(e.target.description)}
          />
          <input 
            placeholder="Valor em reais"
            value={value}
            oncChange={e =>setTittle(e.target.value)}
          />
            

          <button className="button" type="submit">Cadastrar</button>

        </form>
      </div>
    </div>
  )
}
